import React from 'react';
import './App.css';


let id = 0;

function Todo(props) {
  return (
    <li className='item' key={props.todo.text}>
      <input type='checkbox'/>
      <span>{props.todo.text}</span>
      <button> delete </button>
    </li>
 )
}

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      todos: [],
    }
  }


  addTodo(event){
    if(event.key === 'Enter'){
      console.log(event.target.value)
      this.setState({ 
        todos: [
        ...this.state.todos , 
        {id : id++ ,text: event.target.value}
         ], 
      })
      event.target.value =''
    }
  }


  render() {
    return (
      <div className="container">
        <div className="flexContainer">
          <input 
          type='text' 
          placeholder ='type and hit enter to add'
          className ='inputField' 
          onKeyPress={(event) => this.addTodo(event)} />
        </div>
        <ul>
          {this.state.todos.map((todo) => <Todo todo={todo} />)}
        </ul>
      </div>
    )
  }
}

export default App;
